SELECT CG.ID, CG.issueid, CG.AUTHOR, CG.CREATED, CI.ID, CI.groupid, CI.FIELDTYPE, CI.FIELD, CI.OLDVALUE, CI.OLDSTRING, CI.NEWVALUE, CI.NEWSTRING FROM public.changegroup CG INNER JOIN public.changeitem CI ON CG.ID = CI.groupid WHERE (CG.issueid IN ('233951', '233980', '234557', '234040', '234964', '234063', '233952', '233979', '233343', '234654', '234039', '234380', '234966', '233986', '234847', '234950', '236820', '234064', '233993') ) AND (CI.FIELD =  'Story Points’)
{code}
