/* Space Admin sidebar */
.in-page-menu {
  padding: 10px;
}

.in-page-menu-content {
    border-left: 1px solid #CCC;
    padding: 0 0 20px 10px;
}

/* End Space Admin sidebar */

/*Global messages above the header*/
#messageContainer {
    list-style-type: none;
    margin: 0;
    padding: 0;
}

#messageContainer li {
    display: block;
}

#messageContainer .aui-message {
    margin: 0;
    border-radius: 0;
}
/*End global messages above the header*/

#breadcrumbs li.hidden-crumb {
    display: none;
}

/* Metadata stuff just below page title*/
.page-metadata ul {
    overflow: hidden;
    margin: 0;
    padding: 0;
}

.page-metadata ul li {
    list-style: none;
    float: left;
    padding: 0 5px 0 0;
    margin: 0;
    line-height: 1.5;
}

.page-metadata ul a.page-tinyurl span {
    float: left;
    height: 16px;
    width: 10px;
    text-indent: -9999em;
}

.page-metadata ul a.action-view-attachments span.page-metadata-attachments-count {
    margin-left: 3px;
}
/* End metadata stuff*/

/* Page content */
#main {
    margin: 0;
    padding: 20px;
    clear: both;
    min-height: 600px;
}

#title-heading {
    margin: 0;
    padding: 0;
}

#title-text a {
    color: #172B4D;
}

.navBackgroundBox {
    padding: 5px;
    font-size: 22px;
    font-weight: bold;
    text-decoration: none;
}

.simple-confirmation &gt; form &gt; .buttons-container {
    padding-left: 0;
    margin-top: 20px;
}

/* aui-page-layout.css makes this clear, which stuffs up
 the multi-column layout of space admin sidebar */

#content {
    clear: none !important;
}

#content::before {
    display: none !important;
}

/* end aui-page-layout.css overrides */

/*End page content*/

/* Page footer */

.aui-layout #footer .footer-body &gt; ul &gt; li.print-only {
    display: none;
}
/* End page footer */

#com-atlassian-confluence .hidden {
    display: none;
}

/* Progress Page */

/* This doesn&#39;t and shouldn&#39;t be a table */

#status {
    margin: 10px 0;
}

#status table {
    margin: 10px 0;
}

#taskProgressBar {
    background: #ebf2f9;
    /* This border radius will not
       work while it&#39;s still a table */
    border-radius: 3px;
}

#taskGreenBar {
    background: #3b7fc4;
}

#status .smalltext {
    text-align: center;
    color: @mediumGray;
}

#content {
