{code}
{
  "versionNumber": "4.2",
  "buildDate": 1333548000000,
  "buildTimestamp": "2012-04-05T17:08:23.101+1000",
  "buildNumber": "3273",
  "bambooBuildNumber": "${bamboo.buildNumber}",
  "revisionNumber": ""
}
{code}

