{content-reporter:space=@self|type=attachment}
{text-filter:content:file name|exclude=.*jpeg,.*gif,.*jpg,.*GIF,.*JPG,.*png,.*PNG}
{date-filter:track:last view date|minValue=-1y|maxValue=+1d|required=true|format=dd-MMM-yyyy}
