    private AOUser getAO(UserKey userKey)
    {
        AOUser user =  findOnly(select().where("USERNAME = ?", userKey.getStringValue()));
        if (user == null)
        {
            user = ao.create(AOUser.class, new DBParam("USERNAME", userKey.getStringValue()));
