        int rcptNumber = 0;
        int nRcpt = recipientNames.size();

        for (String rcptName : recipientNames)
        {
            ConfluenceUser rcpt = userAccessor.getUserByName(rcptName);

            if (calendarPermissionManager.hasEditSubCalendarPrivilege(rcpt))
            {
                I18NBean userI18n = getI18nBean(rcpt);
                ++rcptNumber;

                try
                {
                    NotificationBuilder notificationBuilder = createNotificationBuilder(rc
                    .
                    .
                    .
{code}

*Workaround*

# Disable the team calendar workbox notification during the ugprade by adding the following in the system properties:
{code}
-Dcom.atlassian.confluence.extra.calendar3.notification.workbox.enabled=false
{code}
# Disable  the workbox notification plugins in Confluence before the upgrade. We highly recommend this to be re-enable after Team Calendar upgrade is finished.
