         for (final Foo foo : foos) {
+            boolean frobbled = service.frobble(foo);
+            if (frobbled) {
+                doSomethingMore();
+            }
         }
     }
+
+    /**
+     * @return Something completely unrelated
+     */
+    static String addedMethod() {
+        return "asdf";
+    }
+
+    /**
+     * Long
+     * Javadoc
+     *
+     * Is
+     * Long
+     *
+     * Lorem ipsum etc.
+     *
+     * @param myParam
+     *         I take a parameter
+     */
