JiraSoapServiceServiceLocator l = new JiraSoapServiceServiceLocator();
JiraSoapService service = l.getJirasoapserviceV2( new URL("YOUR_JIRA_URL_HERE/rpc/soap/jirasoapservice-v2?wsdl')"));
