{code}
public I18nBean getI18nBean(final String additionalBundleName)
    {
        I18nBean i18nBean = (I18nBean) getRequestCache().get(RequestCacheKeys.I18N_BEAN);
        final ResourceBundle resourceBundle = ResourceBundle.getBundle(additionalBundleName, getLocale());
        if ((i18nBean == null) || !i18nBean.getBundles().contains(resourceBundle))
        {
            i18nBean = new I18nBean(getUser(), additionalBundleName);
            getRequestCache().put(RequestCacheKeys.I18N_BEAN, i18nBean);
        }
        return i18nBean;
    }
{code}
