public class FieldLayoutHelper {
...
	private static final FieldManager fieldManager = ManagerFactory.getFieldManager();
	private static final CustomFieldManager customFieldManager = fieldManager.getCustomFieldManager();
	private static final FieldLayoutManager fieldLayoutManager = fieldManager.getFieldLayoutManager();

	public void hide(final EditableSchemeFieldLayout _esfl, final String _field) throws Exception {..}
	public void show(final EditableSchemeFieldLayout _esfl, final String _field) throws Exception {...}
	
	public void moveUp(final EditableSchemeFieldLayout _esfl, final String _field) throws Exception {
		FieldLayoutItem fli = _esfl.getFieldLayoutItem(fieldManager.getOrderableField(getFieldId(_field)));
		_esfl.moveUp(fli);
	}

	public void moveDown(final EditableSchemeFieldLayout _esfl, final String _field) throws Exception {
		FieldLayoutItem fli = _esfl.getFieldLayoutItem(fieldManager.getOrderableField(getFieldId(_field)));
		_esfl.moveDown(fli);
	}

	public void moveTo(final EditableSchemeFieldLayout _esfl, final String _field, final int _position) throws Exception {
		OrderableField orderableField = fieldManager.getOrderableField(getFieldId(_field));
		FieldLayoutItem fli = null;
		if (_position >= 0 && _position < _esfl.getFieldLayoutItems().size()) {
			fli = _esfl.getFieldLayoutItem(orderableField);
			int curPos = fli.getPosition();
			while (curPos != _position) {
				fli = _esfl.getFieldLayoutItem(orderableField);
				if (curPos < _position) {
					_esfl.moveDown(fli);
					curPos++;
				} else {
					_esfl.moveUp(fli);
					curPos--;
				}
			}
		}
	}

	public String getFieldId(final String _fieldName) {
		String fieldId = _fieldName;
		CustomField cf = customFieldManager.getCustomFieldObjectByName(_fieldName);
		if (cf!=null) {
			fieldId = cf.getId(); 
		}		
		return fieldId;
	}
}

